import React from 'react';
import axios from 'axios';
import { Container, Row, Col, Form} from "react-bootstrap";
import "./PopUpForm.css";


class PopUpForm extends React.Component {
  
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
      message: ''
    }
  }

  handleSubmit(e){
    e.preventDefault();
    axios({
      method: "POST", 
      url:"http://api.mbbsadmissionsinabroad.com/index.php", 
      data:  this.state
    }).then((response)=>{
      if (response.data.status === 'success') {
        alert("Message Sent."); 
        this.resetForm()
      } else if (response.data.status === 'fail') {
        alert("Message failed to send.")
      }
    })
  }

  resetForm(){
    this.setState({name: "", email: "", message: ""})
  }
  
  render() {
    return(
      <div>
        <Container>
          <Row>
            <Col xs={6}>
              <form id="contact-form" onSubmit={this.handleSubmit.bind(this)} method="POST">
                <div className="form-group">
                    <label htmlFor="name">Name <span className="asterik__sym">*</span></label>
                    <input type="text" className="form-control" id="name" value={this.state.name} onChange={this.onNameChange.bind(this)} />
                </div>
                <div className="form-group">
                    <label htmlFor="exampleInputEmail1">Email address <span className="asterik__sym">*</span></label>
                    <input type="email" className="form-control" id="email" aria-describedby="emailHelp" value={this.state.email} onChange={this.onEmailChange.bind(this)} />
                </div>
                <div className="form-group">
                    <label htmlFor="number">Phone No <span className="asterik__sym">*</span></label>
                    <input type="number" className="form-control" id="number" value={this.state.number} onChange={this.onNumberChange.bind(this)} />
                </div>
                <div className="form-group">
                    {/*<label htmlFor="option">Select Country <span className="asterik__sym">*</span></label> */}
                    {/*<Form.Control as="select" value={this.state.option} onChange={this.onOptionChange.bind(this)}>
                    <option>Russia</option>
                    <option>Ukraine</option>
                    <option>Armenia</option>
                    <option>Central America Belize</option>
                    <option>France</option>
                    <option>Gerogia</option>
                    <option>Kazakhstan</option>
                    <option>Kyrgystan</option>
                    <option>Philippines</option>
                    <option>Germany</option>
                    <option>Europe</option>
                    <option>Finland</option>
                    <option>Sweden</option>
                    <option>Netherlands</option>
                    <option>Denmark</option>
                    <option>Norway</option>
                    <option>Austria</option>
                    <option>Poland</option>
                    <option>Lithuania</option>
                    <option>Bulgaria</option>
                    <option>Nepal</option>
                  </Form.Control> */}

                  <div>
                    <label>Country</label>
                    <select placeholder="Country" value={this.state.selectedCountry} onChange={this.changeCountry}>
                      <option>--Choose Country--</option>
                      {this.state.countries.map((e, key) => {
                        return <option key={key}>{e.name}</option>;
                      })}
                    </select>
                  </div>

                  <div>
                    <label>State</label>
                    <select placeholder="State" value={this.state.selectedState} onChange={this.changeState}>
                      <option>--Choose State--</option>
                      {this.state.states.map((e, key) => {
                        return <option key={key}>{e.name}</option>;
                      })}
                    </select>
                  </div>
				
                  <div>
                    <label>City</label>
                    <select placeholder="City">
                      <option>--Choose City--</option>
                      {this.state.cities.map((e, key) => {
                        return <option key={key}>{e}</option>;
                      })}
                    </select>
                  </div>
                </div>
                <div className="form-group">
                    <label htmlFor="message">Message <span className="asterik__sym">*</span></label>
                    <textarea className="form-control" rows="5" id="message" value={this.state.message} onChange={this.onMessageChange.bind(this)} />
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
              </form>
            </Col>
            <Col xs={6}>
              <div className="popup__info">
                <h2>Contact Us</h2>
                <p>+91 (934) 166-6667</p>
                <h2>Toll Free No:</h2>
                <p>1800 572-3473</p>
                <h2>Address</h2>
                <p>404, 1st floor, 4th A Cross Rd, HRBR Layout 2nd Block, Kalyan Nagar, Bengaluru - 560043</p>
              </div>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }

  onNameChange(event) {
	  this.setState({name: event.target.value})
  }

  onEmailChange(event) {
	  this.setState({email: event.target.value})
  }

  onMessageChange(event) {
	  this.setState({message: event.target.value})
  }
}

export default PopUpForm;
